mapper.select:
select * from ott_app_channel where is_deleted = 0
and ott_app_channel.name like '%${pagerForm.name}%'
order by ${pagerForm.orderField} ${pagerForm.orderDirection}
order by ott_app_channel.weight desc
limit #{pagerForm.pageStart}, #{pagerForm.numPerPage}

mapper.update:
update 
	ott_channel_app_rel
set
	app_weight = #{app_weight}
where
	app_id = #{app_id}
	and channel_id = #{channel_id}

delete from 
	ott_channel_app_rel
where
	app_id = #{app_id}
	and channel_id = #{channel_id}
